------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
if not (SERVER) then
    return 
end
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
function MODULE:PlayerAuthed(ply, steamid, uniqueid)
    local steamID64 = util.SteamIDTo64(steamid)
    if not (ply:OwnerSteamID64() == steamID64) then
        ply:Kick("Family share accounts are not allowed on this server.")
        for k, v in ipairs(player.GetAll()) do
            if not (v:IsAdmin()) then
                continue
            end
            v:notify("Family share account " .. ply:Name() .. " (" .. steamID64 .. ") attempted to join the server.")
        end
    end
end
