local MODULE = MODULE
------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
lia.command.add(
    "permaRemove",
    {
        adminOnly = true,
        privilege = "Management - Remove Map Entitys",
        onRun = function(client)
            local entity = client:GetEyeTraceNoCursor().Entity
            if IsValid(entity) and entity:CreatedByMap() then
                local entities = MODULE:getData({})
                entities[#entities + 1] = entity:MapCreationID()
                entity:Remove()
                MODULE:setData(entities)
                client:notify("Map entity removed.")
            else
                client:notify("This is not a valid map entity")
            end
        end
    }
)
